package com.bntang666.java8;import java.time.LocalDate;import java.time.MonthDay;import java.util.NoSuchElementException;import java.util.Optional;class Department {    private Employee employee;    public Department(Employee employee) {        this.employee = employee;    }    Employee getEmployee() {        return employee;    }    static String getGirlFriendName(Department department) {        if (department != null) {            Employee employee = department.getEmployee();            if (employee != null) {                Girl girl = employee.getGirlFriend();                if (girl != null) {                    return girl.getName();                }                return "单身汪";            }            return "没有员工";        }        return "部门为空";    }}class Employee {    private Girl girlFriend;    public Employee(Girl girlFriend) {        this.girlFriend = girlFriend;    }    Girl getGirlFriend() {        return girlFriend;    }    public static void main(String[] args) {    }}class Girl {    private String name;    public Girl(String name) {        this.name = name;    }    public Girl() {    }    String getName() {        return name;    }    public static void main(String[] args) {        LocalDate birthday = LocalDate.of(2001, 8, 10);        MonthDay monthDay = MonthDay.of(birthday.getMonth(), birthday.getDayOfMonth());        MonthDay currentMonthDay = MonthDay.from(LocalDate.now());        if (currentMonthDay.equals(monthDay)) {            System.out.println("happy birthday!");        }    }    static String getGirlFriendName(Department department) {        Optional<Department> opt = Optional.ofNullable(department);        return opt.map(Department::getEmployee)                .map(Employee::getGirlFriend)                .map(Girl::getName)                .orElseThrow(NoSuchElementException::new);    }}